const Utils = require("../core/Utils");
const Type = require("./Type");
class AliasType extends Type{
    constructor(inherit,target){
        super("$AliasType",inherit)
        this.isAliasType = true;
        this.target = target;
    }
    is( type ){
        if( !type || !(type instanceof Type) )return false;
        if( type.isGenericValueType ){
            type = type.value;
            if( !type ){
                return true;
            }
        }
        if( this === type || type.isNullableType || type.isAnyType)return true;
        if( this.toString()==="object" ){
            return !Utils.isScalar(type) && this.inherit.is( type );
        }
        return this.inherit.is( type );
    }
    toString(){
        return this.target.value();
    }
}
module.exports = AliasType;