const Type = require("./Type");
class ClassType extends Type{
    constructor(inherit,module,target){
        super("$ClassType",inherit)
        this.isClassType = true;
        this.module = module;
        this.target = target;
    }
    get id(){
        return this.inherit.id;
    }
    is( type){
        if( !type || !(type instanceof Type) )return false;
        if( type.isGenericValueType ){
            type = type.value;
            if( !type ){
                return true;
            }
        }
        if( type === this || type.isNullableType || type.isAnyType )return true;
        if( type.isClassType ){
            return this.module === type.module;
        }
        return this.module === type && this.inherit.is( type );
    }

    toString(){
        return `class<${this.target.value()}>`;
    }
}
module.exports = ClassType;