const Type = require("./Type");
class NullableType extends Type{
    constructor(inherit){
        super("$NullableType",inherit)
        this.isNullableType = true;
    }
    is( type ){
        if( !type || !(type instanceof Type) )return false;
        return true;
    }
    toString(){
        return "null";
    }
}
module.exports = NullableType;