const Type = require("./Type");
class VoidType extends Type{
    constructor(){
        super("$void");
        this.isVoidType = true;
    }
    is( type ){
        if(!(type && type instanceof Type))return false;
        if( type.isGenericValueType ){
            type = type.value;
            if( !type ){
                return true;
            }
        }
        if(type === this)return true;
        return false;
    }
    toString(){
        return 'void';
    }
}
module.exports = VoidType;